.tabs-with-dropdowns {
  width: 100%;
}

.tab-buttons {
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
}

.tab-container {
  position: relative;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
}

.tab-buttons button {
  padding: 100px 30px;
  height: 60px;
  font-size: 20px;
  cursor: pointer;
  background: none;
  border: none;
  border-left: 1px solid #fff;
  transition: border-bottom 0.3s ease;
  background-color: #800000;
  color: #fff;
  display: flex;
  align-items: center;
  justify-content: space-between;
}


.dropdown-icon {
  margin-left: 3px;
}

.tab-dropdown {
  position: absolute;
  top: 100%;
  left: 0;
  background: #fff;
  border: 1px solid #ccc;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
  z-index: 1000;
  width: max-content;
}

.tab-dropdown ul {
  list-style: none;
  margin: 0;
  padding: 0;
}

.tab-dropdown li {
  padding: 10px;
  cursor: pointer;
}

.tab-dropdown li:hover {
  background: #f1f1f1;
}

.tab-dropdown li a {
  display: block;
  text-decoration: none;
  color: #000;
}

.tab-content {
  padding: 20px;
  padding-top: 0 !important;
  /* border: 1px solid #ccc; */
  display: flex;
  justify-content: center;
  align-items: center;
  border-top: none;
}

.tab-panel {
  display: none;
  width: 1225px;
}

.tab-panel.active {
  display: block;
}

/* Media Queries for responsive design */
@media (max-width: 1226px) {
  .tab-buttons {
    flex-direction: column; /* Stack tabs vertically on small screens */
  }

  .tab-buttons button {
    width: 100%;
    text-align: left; /* Align text to the left for better readability */
  }

  .tab-dropdown {
    position: absolute; /* Keep dropdown position absolute */
    top: 100%; /* Ensure it displays below the tab */
    left: 0;
    width: 100%; /* Ensure the dropdown takes the full width */
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15); /* Add box-shadow for visual separation */
    z-index: 1000; /* Ensure dropdown is above other elements */
  }

  .tab-dropdown ul {
    border-top: none; /* Remove the border-top for cleaner look */
  }
}
